import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "./axios.js";
export const authintificationFetch = createAsyncThunk("/login", async () => {
  const data = axios.get("/users");
  return data;
});

const initialState = {
  data: null,
  status: "loading",
};
const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {},
  extraReducers: {
    [authintificationFetch.pending]: (state) => {
      state.data = null;
      state.status = "loading";
    },
    [authintificationFetch.fulfilled]: (state, action) => {
      state.data = action.payload;
      state.status = "loaded";
    },
    [authintificationFetch.rejected]: (state) => {
      state.data = null;
      state.status = "error";
    },
  },
});
export const authReducer = authSlice.reducer;
