import { useSelector, useDispatch } from "react-redux";
import styles from "./userPage.module.css";
import { isAuth } from "../usersPage/usersSlice.js";
import axios from "../../axios.js";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";

const UserPage = () => {
  const authPassed = useSelector(isAuth);
  const [userData, setData] = useState();
  const { id } = useParams();
  useEffect(() => {
    axios
      .get(`/users/${id}`)
      .then((res) => {
        setData(res.data);
      })
      .catch((err) => {
        console.error(err);
        alert("Some error during the profile loading");
      });
  }, [id]);

  if (!authPassed) {
    return (
      <>
        <h1 className={styles.errorMessage}>No access!</h1>
      </>
    );
  }
  return (
    <>
      <div className={styles.rootUser}>
        <h3 className={styles.userName}>{userData.name}</h3>
        <img src="src" alt="pfp" />
        <p className={styles.userDescription}>{userData.email}</p>
      </div>
    </>
  );
};

export default UserPage;
